import React from "react";
import "./ModalError.css";

const ModalError = ({ inputValue, onSubmit, setShow}) => {
{setShow

  if (inputValue.username === 0 || inputValue.age === 0) {
    return (
      <div className="modal-error" >
        <p>Please enter a valid name and age(non-empty values)</p>
        <button >Okey</button>
      </div>
    );
  } else if (inputValue.age < 0) {
    return (
      <div className="modal-error">
        <p>Please enter a valid age (more then 0)</p>
        <button >Okey</button>
      </div>
    );
  }
}
};

export default ModalError;
