import React, { useState } from "react";
// import ModalOverlayAge from "./ModalOverlayAge";
// import ModalOverlayName from "./ModalOverlayName";

const AddUser = () => {
    const [value, setValue] = useState({
        user: "",
        age: null,
    })
    const [x, setX] = useState([])
//   const validation = (event) => {
//     if (name === "") {
//       return <ModalOverlayName />;
//     } else if (age < 0 || age === "") {
//       return <ModalOverlayAge />;
//     }
//   };

  const onChange = (event) => {
        const {name, value} = event.target
        setValue( {...value, [name]: value})
  }

  const onSubmitHandler = (event) => {
    event.preventDefault();
    setX([...x, value])
    console.log("x", x);
  };
  return (
    <div style={{margin: "50px auto", width: "100px"}}>
      <form  onSubmit={onSubmitHandler}>
        <label>Username</label>
        <br />
        <input type="Text" name="username" onChange={onChange}/>
        <br />
        <label>Age (Years)</label>
        <br />
        <input type="number" name="age" onChange={onChange}/>
        <br />
        <button>Add User</button>
      </form>
    </div>
  );
};

export default AddUser;
