import React from "react";
import ModalOverlayAge from "./ModalOverlayAge";
import ModalOverlayName from "./ModalOverlayName";

const AddUser = () => {
  const validation = (event) => {
    const { name, age } = event.target.name;
    if (name === "") {
      return <ModalOverlayName />;
    } else if (age < 0 || age === "") {
      return <ModalOverlayAge />;
    }
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
  };
  return (
    <div style={{margin: "auto", wi}}>
      <form>
        <label>Username</label>
        <br />
        <input type="Text" name="username" />
        <br />
        <label>Age (Years)</label>
        <br />
        <input type="number" name="age" />
        <br />
        <button onSubmit={onSubmitHandler}>Add User</button>
      </form>
    </div>
  );
};

export default AddUser;
