import React from "react";
import "./ModalError.css";

const ModalError = ({username, age}) => {
  return (
    <>
      {username.length < 0 && age.length < 0 ? <div className="modal-error">
        <p>Please enter a valid name and age(non-empty values)</p>
        <button>Okey</button>
      </div> : username.age < 0 ?
      <div className="modal-error">
        <p>Please enter a valid age (more then 0)</p>
        <button>Okey</button>
      </div> : <span style={{display: "none"}}></span>
      }
    </>
  );
};

export default ModalError;
