import React, { useState } from "react";
// import ModalOverlayAge from "./ModalOverlayAge";
// import ModalOverlayName from "./ModalOverlayName";

const AddUser = ({onChange, onSubmit, inputValue}) => {
    
  return (
    <div style={{margin: "50px auto", width: "100px"}}>
      <form  onSubmit={onSubmit}>
        <label>Username</label>
        <br />
        <input type="Text" value={inputValue.username} name="username" onChange={onChange}/>
        <br />
        <label>Age (Years)</label>
        <br />
        <input type="number" value={inputValue.age} name={inputValue.age} onChange={onChange}/>
        <br />
        <button>Add User</button>
      </form>
    </div>
  );
};

export default AddUser;
